Router.route('/:invoiceId/:milestoneId')
    .get(async (req, res) => {
        const { invoiceId, milestoneId } = req.params;

        try {
            const { milestones, customerEmail, marchantName } = await InvoiceModel.findOne({ _id: invoiceId });
